namespace DotNetTask.UnitTest
{
    public class ApplicationControllerTest
    {
        private readonly ApplicationController _controller;
        private readonly IApplicationService _appService;
        MapperConfiguration mapperConfig = new MapperConfiguration(
       cfg =>
       {
           cfg.AddProfile(new MappingProfile());
       });

        public ApplicationControllerTest(

           )
        {


            //_configuration =  configuration;
            IConfiguration _configuration = new ConfigurationBuilder().Build();

            var url = "https://localhost:8081";
            var primaryKey = "C2y6yDjf5/R+ob0N8A7Cgv30VRDJIWEHLM+4QDU5DE2nQ9nDuVTqobD4b8mGGyPMbIZnqyMsEcaGQy67XIw/Jw==";
            var dbName = "ProgramDB";
            var acontainerName = "AContainer";


            var cosmosClient = new CosmosClient(url, primaryKey);
            _appService = new ApplicationService(cosmosClient, dbName, acontainerName);
            IMapper mapper = new Mapper(mapperConfig);
            _controller = new ApplicationController(mapper, _appService, _configuration);

        }

        [Fact]
        public async void List_WhenCalled_ReturnsOkResult()
        {
            // Act
            var okResult = await _controller.List();


            // Assert
           
            Assert.IsType<OkObjectResult>(okResult);

        }

        [Fact]
        public async void Get_WhenCalled_ReturnsItemsById()
        {
            //Arrange
            var testGuid = "983f9e7a-7abe-4d81-b4bc-3802125c790b";

            // Act
            var okResult = await _controller.Get(testGuid);

            // Assert
            Assert.IsType<OkObjectResult>(okResult);
        }


        [Fact]
        public async void Edit_WhenCalled_ReturnsNoContentResult()
        {
            //Arrange

            var QuestionChoice = new List<QuestionChoiceDto>()
                {
                    new QuestionChoiceDto {Type ="Testing"}

                }.ToList();

            var Question = new List<AdditionalQuestionDto>()
                {
                    new AdditionalQuestionDto { Question ="Testing Question 1",Choice =QuestionChoice },

                }.ToList();
            var pinfo = new List<PersonalInfoDto>() { 
              new PersonalInfoDto{Internal = true,ShowHide=true,Name="FirstName"},
              new PersonalInfoDto{Internal = true,ShowHide=true,Name="LastName"}
            }.ToList();

            var pfo = new List<ProfileInfoDto>() {
              new ProfileInfoDto{ShowHide=true,Name="Education",Mandatory=true},
              new ProfileInfoDto{ShowHide=true,Name="Experience",Mandatory=true}
            }.ToList();

            var myData = new ApplicationFormDto
            {
                Id = "983f9e7a-7abe-4d81-b4bc-3802125c790b",
                AdditionalQuestion = Question,
                CoverPhotoBase64String = "",
                ProgramId = "7a4c8271-2d7f-4250-a99f-6489d28efb2b",
                PersonalInformation = pinfo,
                Profile = pfo
                
            };

            // Act
            var createdResponse = await _controller.Edit(myData);
            // Assert
            Assert.IsType<NoContentResult>(createdResponse);


        }
    }
}
